import React, { useState, useEffect, useContext } from "react";
import Axios from "axios";
import { Redirect } from 'react-router-dom'
import { AuthContext } from "./Auth";

const Techer = () => {
   const { currentUser, setCurrentUser, status, setStatus } = useContext(AuthContext);

   const [nameList, setNameList] = useState([]);
   const [error, setError] = useState(null);
   const [student, setStudent] = useState(null)
   useEffect(async () => {
      await getNameList();
   }, []);

   const getNameList = async () => {
      await Axios.get("http://localhost:3001/namelist").then((response) => {
         setNameList(response.data);
      });
   };
   const Logout = () => {
      Axios.get("http://localhost:3001/logout").then((response) => {
         if (response.data.massage) {
            setCurrentUser(null);
            setStatus(null);
         }
      });
   };
   const postScore = async (SID) => {
      console.log(SID);
      await Axios.post("http://localhost:3001/score", { student: SID, })
         .then((response) => {
            if (response.data.massage) {
               setError(response.data.massage);
            } else {
               setStudent(response.data)
               console.log(response.data)
               console.log(student)
            }
         });
   };
   return (
      <div>
         <nav className="navbar login-header">
            <h1 className="titleBar"> Teacher </h1>
            <button className="btn btn-secondary btn-md titleBar2"
               onClick={Logout}> logout </button>
         </nav>
         <div className="container">
            <div className="row">
               <div className="col-sm-10 col-md-7 col-md-5 col-lg-10 mx-auto">
                  <div className="card card-signin my-5 card border-dark mb-3">
                     <div className="card-body">
                        {error &&
                           <p>{error}</p>
                        }

                        {(!student) && nameList.map((val, key) => {
                           return (<ul className="list-group">
                              <li className="list-group-item d-flex align-items-center">
                                 <div className="fw-bold col mx-auto">
                                    <button className="badge btn btn-secondary btn-sm space" onClick={() => { postScore(val.username) }} > ... </button>
                                    {val.username}
                                 </div>
                                 <span className="badge bg-primary rounded-pill">Done</span>
                              </li>
                           </ul>

                           );
                        })}

                        {(!!student) &&
                           <div>
                              
                              <div>
                                 <h2 class="mid">{student.id} </h2>
                                 <button className="btn btn-secondary " onClick={() => { setStudent(null) }} > back </button>
                              </div>
                              <table className="table bottomGraph">
                                 <thead>
                                    <tr>
                                       <th scope="col">#</th>
                                       <th scope="col">SECTION 1</th>
                                       <th scope="col">SECTION 2</th>
                                       <th scope="col">SECTION 3</th>
                                    </tr>
                                 </thead>
                                 <tbody>
                                    <tr>
                                       <th scope="row">TEST 1</th>
                                       <td>Mark</td>
                                       <td>Otto</td>
                                       <td>@mdo</td>
                                    </tr>
                                    
                                 </tbody>
                              </table>
                           </div>
                        }

                     </div>
                  </div>
               </div>
            </div>
         </div>
      </div>
   );
}

export default Techer;
