import Axios from "axios";
import  { Redirect } from 'react-router-dom'
const Home = ()=>  {
   const [loginStatus, setLoginStatus] = useState({
      isLogin: false,
      currentUser: null,
      error: null
   });
   const init = async () => {
      await Axios.get("http://localhost:3001/login").then((response) => {
         if (response.data.loggedIn == true) {
            console.log("55555");
            setLoginStatus({
               isLogin: true,
               currentUser: response.data.user.username,
               error: null
            });

         } else {
            console.log("6666666");
            setLoginStatus({
               isLogin: false,
               currentUser: null,
               error: null
            });
         }
      });
   }
   useEffect(() => {
      init();
    }, []);

   

   if (loginStatus.isLogin) {
      return (
         
         <div className="login-page">
            
            <header className="login-header">
               <h1> Home </h1>
            </header>
            <div className="bok-login">
            
   
            </div>
         </div>
      );
   } 
   else { 
      console.log("55555");
      return ("55555");
   }
  }
  
  export default Home;
