import React, { useState, useEffect, useContext } from "react";
import Axios from "axios";
import { AuthContext } from "./Auth";

const Student = () => {
   const { currentUser, setCurrentUser, status, setStatus } = useContext(AuthContext);
   const [student, setStudent] = useState(null);
   const [listTest, setListTest] = useState([]);
   let answer = [];
   const Logout = () => {
      Axios.get("http://localhost:3001/logout").then((response) => {
         if (response.data.massage) {
            setCurrentUser(null);
            setStatus(null);
         }
      });
   };
   useEffect(() => {
      init();
   }, []);

   const init = async () => {
      await Axios.get("http://localhost:3001/score")
         .then(async (response) => {
            console.log(response.data)
            await setListTest(response.data)
         })

      await Axios.get("http://localhost:3001/student")
         .then(async (response) => { await setStudent(response.data) })

   }
   const handleStatus = event => {
      console.log(event.target.name);
      console.log(event.target.value);
      answer[event.target.name] = event.target.value
      console.log(answer);
   }
   const getAnimalsContent = () => {
      let content = [];
      for (let i = 0; i < 20; i++) {
        
        content.push(
         <div onChange={handleStatus.bind(this)}>
            <br/>
            <div className="form-check form-check-inline">
               <input className="form-check-input" id = "choice1" type="radio" value="1" name={i} />
               <label class="form-check-label" for="choice1">1</label> 
            </div>
            <div className="form-check form-check-inline">
               <input className="form-check-input" id = "choice2"type="radio" value="2" name={i} /> 
               <label class="form-check-label" for="choice2">2</label>
            </div>
            <div className="form-check form-check-inline">
               <input className="form-check-input" id = "choice3"type="radio" value="3"name={i} /> 
               <label class="form-check-label" for="choice3">3</label>
            </div>
            <div className="form-check form-check-inline">
               <input className="form-check-input" id = "choice4"type="radio" value="4"name={i} /> 
               <label class="form-check-label" for="choice4">4</label>
            </div>
         </div>);
      }
      return content;
    };



   return (
      <div>
         <nav className="navbar login-header">
            <h1 className="titleBar"> Student </h1>
            <button className="btn btn-secondary btn-md titleBar2"
               onClick={Logout}> logout </button>
         </nav>
         <div className="container">
            <div className="row">
               <div className="col-md-8  col-sm-10 col-lg-10 mx-auto">
                  <div className="card card-signin my-5 card border-dark mb-3">
                     <div className="card-body">

                        { listTest && listTest[0] &&
                        <div>
                           <h3>TEST {listTest[0].Tid}</h3>
                           {getAnimalsContent()}
                           <button className="btn btn-signin btn-secondary" type="submit" onClick={() => { console.log(listTest); answer = []; }} >submit</button>
                        </div>
                        }
                        {listTest != 0 &&
                        <h3> ทำข้อสอบครบเเล้ว </h3>
                        }
                        

                     </div>
                  </div>
               </div>
            </div>
         </div>

      </div >
   );
}

export default Student;
