import React, { useState, useEffect, useContext } from "react";
import Axios from "axios";
import { AuthContext } from "./Auth";

const Student = () => {
   const { currentUser, setCurrentUser, status, setStatus } = useContext(AuthContext);
   const [student, setStudent] = useState(null);
   const [listTest, setListTest] = useState([]);
   const answer = [];
   const Logout = () => {
      Axios.get("http://localhost:3001/logout").then((response) => {
         if (response.data.massage) {
            setCurrentUser(null);
            setStatus(null);
         }
      });
   };
   useEffect(() => {
      init();
   }, []);

   const init = async () => {
      await Axios.get("http://localhost:3001/score")
         .then(async (response) => {
            console.log(response.data)
            await setListTest(response.data)
         })

      await Axios.get("http://localhost:3001/student")
         .then(async (response) => { await setStudent(response.data) })

   }
   const handleStatus = event => {
      console.log(event.target.name);
      console.log(event.target.value);
      answer[event.target.name] = event.target.value
      console.log(answer);
   }


   return (
      <div>
         <nav className="navbar login-header">
            <h1 className="titleBar"> Student </h1>
            <button className="btn btn-secondary btn-md titleBar2"
               onClick={Logout}> logout </button>
         </nav>
         <div className="container">
            <div className="row">
               <div className="col-md-8  col-sm-10 col-lg-10 mx-auto">
                  <div className="card card-signin my-5 card border-dark mb-3">
                     <div className="card-body">
                        <button className="btn btn-signin btn-secondary" type="submit" onClick={() => { console.log(listTest) }} >print</button><br/>
                        <br/>
                        <div onChange={handleStatus.bind(this)}>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "11" type="radio" value="1" name="0"  />
                              <label class="form-check-label" for="11">1</label> 
                           </div>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "12"type="radio" value="2" name="0" /> 
                              <label class="form-check-label" for="12">2</label>
                           </div>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "13"type="radio" value="3" name="0" /> 
                              <label class="form-check-label" for="13">3</label>
                           </div>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "14"type="radio" value="4" name="0" /> 
                              <label class="form-check-label" for="14">4</label>
                           </div>
                        </div>
                        <br/>
                        
                        <div onChange={handleStatus.bind(this)}>
                        2.  
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "11" type="radio" value="1" name="1"  />
                              <label class="form-check-label" for="11">1</label> 
                           </div>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "12"type="radio" value="2" name="2" /> 
                              <label class="form-check-label" for="12">2</label>
                           </div>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "13"type="radio" value="3" name="3" /> 
                              <label class="form-check-label" for="13">3</label>
                           </div>
                           <div className="form-check form-check-inline">
                              <input className="form-check-input" id = "14"type="radio" value="4" name="4" /> 
                              <label class="form-check-label" for="14">4</label>
                           </div>
                           
                        </div>
                     </div>
                  </div>
               </div>
            </div>
         </div>

      </div >
   );
}

export default Student;
