import React, { useState, useEffect, useContext } from "react";
import Axios from "axios";
import { AuthContext } from "./Auth";

const Student = () => {
   const { currentUser, setCurrentUser, status, setStatus } = useContext(AuthContext);
   const [student, setStudent] = useState(null);
   const [listTest, setListTest] = useState([]);
   const Logout = () => {
      Axios.get("http://localhost:3001/logout").then((response) => {
         if (response.data.massage) {
            setCurrentUser(null);
            setStatus(null);
         }
      });
   };
   useEffect(() => {
      init();
   }, []);
   const init = async () => {
      await Axios.post("http://localhost:3001/score", { student: currentUser })
         .then(async (response) => { await setListTest(response.data) })

      await Axios.get("http://localhost:3001/student")
         .then(async (response) => { await setStudent(response.data) })

   }


   return (
      <div>
         <nav className="navbar login-header">
            <h1 className="titleBar"> Student </h1>
            <button className="btn btn-secondary btn-md titleBar2"
               onClick={Logout}> logout </button>
         </nav>
         <div className="container">
            <div className="row">
               <div className="col-sm-10 col-md-7  col-lg-10 mx-auto">
                  <div className="card card-signin my-5 card border-dark mb-3">
                     <div className="card-body">
                     <button className="btn btn-signin btn-secondary" type="submit" onClick={() => { console.log(listTest) }} >Add Pretest</button>
                     </div>
                  </div>
               </div>
            </div>
         </div>
         <button className="btn btn-signin btn-secondary" type="submit" onClick={() => { console.log(listTest) }} >Add Pretest</button>

      </div >
   );
}

export default Student;
