import React, { useState, useEffect } from "react";
import Axios from "axios";
import  { Redirect } from 'react-router-dom'
;
const Login = () => {
   const [status, setStatus] = useState("");
   const [username, setUsername] = useState('')
   const [password, setPassword] = useState('')
   const [loginStatus, setLoginStatus] = useState({
      isLogin: false,
      currentUser: null,
      error: null
   });
   Axios.defaults.withCredentials = true;

   const handleUsername = event => {
      setUsername(event.target.value);
   }

   const handlePassword = event => {
      setPassword(event.target.value); 
   }


   const postLogin = () => {

      Axios.post("http://localhost:3001/login", {
         username: username,
         password: password,
      }).then((response) => {

         if (response.data.message) {
            setLoginStatus({
               isLogin: false,
               currentUser: null,
               error: response.data.message
            });
         } else {
            setLoginStatus({
               isLogin: true,
               currentUser: response.data.username,
               error: null
            });
         }
      });
   };
   useEffect(() => {
      Axios.get("http://localhost:3001/login").then((response) => {
         if (response.data.loggedIn == true) {
            setLoginStatus({
               isLogin: true,
               currentUser: response.data.user.username,
               error: null
            });

         } else {
            setLoginStatus({
               isLogin: false,
               currentUser: null,
               error: null
            });
         }
      });
   }, []);
   
   if (loginStatus.isLogin) {
      return <Redirect to='/' />;
   } else {
      return (
         <div className="login-page">
            <header className="login-header">
               <h1> Login </h1>
            </header>
            <div className="container">
               <div className="row">
                  <div className="col-sm-9 col-md-7 col-lg-5 mx-auto ">
                     <div className="card card-signin my-5">
                        <div className="card-body">
                           <h5 className="card-title text-center">Login</h5>
                           {loginStatus.error &&
                              <p>{loginStatus.error}</p>
                           }
                           <div className="form-signin">
                              <div className="form-label-group">
                                 <input type="text" id="inputUsername" className="form-control"
                                    placeholder="Username" onChange={handleUsername} />
                                 <label for="inputUsername">Username</label>
                              </div>

                              <div class="form-label-group">
                                 <input type="password" id="inputPassword" className="form-control"
                                    placeholder="Password  " onChange={handlePassword} />
                                 <label for="inputPassword">Password</label>
                              </div>
                              <div id="register-link" >
                                 <a href="/register" className="text-info">Register here</a>
                              </div>
                              <button className="btn btn-signin btn-secondary" type="submit" onClick={postLogin}>LOG IN</button>
                           </div>
                        </div>
                     </div>
                  </div>
               </div>
            </div>
         </div>
      );
   }
}

export default Login;
