import React, { useState, useEffect } from 'react'
import Axios from "axios";
const AuthContext = React.createContext();

export const AuthProvider = ({ children }) => {
   Axios.defaults.withCredentials = true;
   const postLogin = async () => {
      await Axios.get("http://localhost:3001/login", {
      }).then(async (response) => {

         if (response.data.message) {
            await setError(response.data.message);
         } else {
            await setCurrentUser(response.data.username)
            await setStatus(response.data.status)
         }
      });
   };
   const [loading, setLoaing] = useState(false);
   console.log("currentUser")
   const [currentUser, setCurrentUser] = useState(getUsername());
   console.log(currentUser)
   const [status, setStatus] = useState(getStatus());
   console.log(status)
   useEffect(async () => {
      await Axios.get("http://localhost:3001/login").then((response) => {
         if (response.data.loggedIn) {
            setCurrentUser(response.data.user.username);
            //console.log("sdkflsdf", response.data.user.status);
            setStatus(response.data.user.status);
         }
      });

   }, [])

   return (
      <AuthContext.Provider value={{ currentUser, setCurrentUser, status, setStatus }}>
         {children}
      </AuthContext.Provider>
   )
}
export { AuthContext };
