import React, { useState, useEffect , useConstructor} from "react";
import Axios from "axios";
import { Redirect } from 'react-router-dom'
const Home = () => {
   useConstructor(() => {
      console.log(
        "This only happens ONCE and it happens BEFORE the initial render."
      );
    });
   const [loading, setLoading] = useState(false);
   const [isLogin, setIsLogin] = useState(false);
   const [currentUser, setCurrentUser] = useState(null);
   
   Axios.defaults.withCredentials = true;
   
   useEffect(async () => {
      await init();
     
      setLoading(true);
      
   }, []);

   const init = async () => {
      await Axios.get("http://localhost:3001/login").then( (response) => {
         
         setIsLogin(response.data.loggedIn);
         if (response.data.loggedIn) {
            setCurrentUser(response.data.user.username);
         }
      });
   }
   console.log("load" ,loading);
   if (loading){
      if (isLogin) {
         return (
            
            <div className="login-page">

               <header className="login-header">
                  <h1> Home </h1>
               </header>
               <div className="bok-login">
               </div>
            </div>
         );
      }
      else{
         return (<Redirect to = "/login"/>);
      }
   }
   else{
      return("");
   }

   
}

export default Home;
