import React, { useState, useEffect, useContext } from "react";
import Axios from "axios";
import { Redirect } from 'react-router-dom'
import { AuthContext } from "./Auth";

const Techer = () => {
   const { currentUser, setCurrentUser, status, setStatus } = useContext(AuthContext);
   const [nameList, setNameList] = useState([]);
   const [error, setError] = useState(null);
   const [showpage, setShowPage] = useState("listname")
   const [student, setStudent] = useState(null)
   const [Sid, setSid] = useState("")
   useEffect(async () => {
      await getNameList();
   }, []);

   const getNameList = async () => {
      await Axios.get("http://localhost:3001/namelist").then((response) => {
         setNameList(response.data);
      });
   };
   const Logout = () => {
      Axios.get("http://localhost:3001/logout").then((response) => {
         if (response.data.massage) {
            setCurrentUser(null);
            setStatus(null);
         }
      });
   };
   const postScore = async (SID) => {
      await setSid(SID);
      console.log(Sid);
      await Axios.post("http://localhost:3001/score", { student: SID, })
         .then(async (response) => {
            if (response.data.massage) {
               await setError(response.data.massage);
            } else {
               await setStudent(response.data)
               await setShowPage("score")
               console.log(response.data)
               console.log(student)

            }
         });
   };
   return (
      <div>
         <nav className="navbar login-header">
            <h1 className="titleBar"> Teacher </h1>
            <button className="btn btn-secondary btn-md titleBar2"
               onClick={Logout}> logout </button>
         </nav>
         <div className="container">
            <div className="row">
               <div className="col-sm-10 col-md-7  col-lg-10 mx-auto">
                  <div className="card card-signin my-5 card border-dark mb-3">
                     <div className="card-body">
                        {error &&
                           <p>{error}</p>
                        }

                        {(showpage === "listname") && nameList.map((val, key) => {
                           return (<ul className="list-group">
                              <li className="list-group-item d-flex align-items-center">
                                 <div className="fw-bold col mx-auto">
                                    <button className="badge btn btn-secondary btn-sm space" onClick={() => { postScore(val.username) }} > ... </button>
                                    {val.username}
                                 </div>
                                 <button className="badge  btn btn-primary btn-sm rounded-pill" onClick={() => { setShowPage("addpretest") }} > Add Pretest  </button>
                              </li>
                           </ul>

                           );
                        })}

                        {(showpage === "score") &&
                           <div>

                              <div>
                                 <h2 class="mid">{Sid} </h2>
                                 <button className="btn btn-secondary " onClick={() => { setStudent([]); setShowPage("listname") }} > back </button>
                              </div>
                              <table className="table bottomGraph">
                                 <thead>
                                    <tr>
                                       <th scope="col"></th>
                                       <th scope="col">SECTION 1</th>
                                       <th scope="col">SECTION 2</th>
                                       <th scope="col">SECTION 3</th>
                                    </tr>
                                 </thead>
                                 <tbody>
                                    {student.map((val, key) => {
                                       return (
                                          <tr>
                                             <th scope="row">TEST {val.Tid}</th>
                                             <td>{val.session1}</td>
                                             <td>{val.session2}</td>
                                             <td>{val.session3}</td>
                                          </tr>
                                       );
                                    })}
                                 </tbody>
                              </table>
                              <div id="curve_chart"></div>
                           </div>
                        }
                        {(showpage === "addpretest") &&
                           <div>
                              <h5 className="card-title text-center">Login</h5>
                           {error &&
                              <p>{error}</p>
                           }
                           <div className="form-signin">
                              <div className="form-label-group">
                                 <input type="text" id="inputUsername" className="form-control"
                                    placeholder="Username" />
                                 <label for="inputUsername">Username</label>
                              </div>

                              <div class="form-label-group">
                                 <input type="password" id="inputPassword" className="form-control"
                                    placeholder="Password  "  />
                                 <label for="inputPassword">Password</label>
                              </div>
                              <div id="register-link" >
                                 <a href="/register" className="text-info">Register here</a>
                              </div>
                              <button className="btn  btn-secondary" type="submit" >LOG IN</button>
                           </div>

                           </div>
                        }


                     </div>
                  </div>
               </div>
            </div>
         </div>
      </div>
   );
}

export default Techer;
