import React, { useState, useEffect } from "react";
import Axios from "axios";
const Login = ({ setSession }) => {
   const [status, setStatus] = useState("");
   const [username, setUsername] = useState('')
   const [password, setPassword] = useState('')
   const [loginStatus, setLoginStatus] = useState('');

   const handleUsername = event => {
      setUsername(event.target.value);
      console.log(username)
   }

   const handlePassword = event => {
      setPassword(event.target.value);
      console.log(password);
   }
   const getLoginstate = () => {
      console.log(loginStatus);
   }

   const postLogin = async () => {
      alert(loginStatus);
      await Axios.post("http://localhost:3001/login", {
         username: username,
         password: password,
      }).then((response) => {

         if (response.data.message) {
            setSession({
               isLogin: false,
               currentUser: null,
               error: response.data.message
            });
            alert(loginStatus);
         }
         else {
            setSession({
               isLogin: true,
               currentUser: response.data.user
            });

         }
      });
      alert(loginStatus);

   };
   return (
      <div className="login-page">
         <header className="login-header">
            <h1> Login </h1>
         </header>
         <div className="container">
            <div className="row">
               <div className="col-sm-9 col-md-7 col-lg-5 mx-auto ">
                  <div className="card card-signin my-5">
                     <div className="card-body">
                        <h5 className="card-title text-center">Login</h5>
                        {loginStatus != '' &&
                           <p> {loginStatus}</p>
                        }
                        <form className="form-signin">
                           <div className="form-label-group">
                              <input type="text" id="inputEmail" className="form-control"
                                 placeholder="Username" required autofocus onChange={handleUsername} />
                              <label for="inputEmail">Username</label>
                           </div>

                           <div class="form-label-group">
                              <input type="password" id="inputPassword" className="form-control"
                                 placeholder="Password  " required onChange={handlePassword} />
                              <label for="inputPassword">Password</label>
                           </div>
                           <div id="register-link" >
                              <a href="/register" className="text-info">Register here</a>
                           </div>
                           <button className="btn btn-signin btn-secondary" type="submit" onClick={postLogin}>LOG IN</button>
                        </form>
                        <button className="btn btn-signin btn-secondary" type="submit" onClick={getLoginstate}>LOG IN</button>
                     </div>
                  </div>
               </div>
            </div>
         </div>
      </div>
   );
}

export default Login;
